mapper.select:
select ex.id, ex.referencia_id as referenciaId, ex.nome, ex.descricao, ex.sequencia, ex.valor, ex.peso, ex.dthr_emissao as dthrEmissao, ex.status, ex.ativo from exemplos ex where ex.dthr_emissao >= #{dthrInicio} and ex.dthr_emissao <= #{dthrFim}
and ex.nome like CONCAT('%', #{nome}, '%')
and ex.status = #{status}
order by ${columnType} ${orderType}
